import React, { Component } from 'react';
import { Card } from 'primereact/card';
import C3Chart from 'react-c3js';
import 'c3/c3.css';
import axios from 'axios';
class Charts extends Component {

    constructor() {
        super();
        this.state = {

            json: [],
            selectedColumns: [],


            keys: {
                x: 'reportDate',
        
                value: ['totalConfirmed', 'deaths.total']
              },
              names: {
                totalConfirmed: 'Infected',
                'deaths.total': 'Deaths'
                
            },

         

        };


        this.state.prdel = {

            json: [
                {name: 'das', upload: 200, download: 200, total: 400},
                {name: 'dsad', upload: 100, download: 300, total: 400},
                {name: 'fsf', upload: 300, download: 200, total: 500},
                {name: 'wdasdsa', upload: 400, download: 100, total: 500},
            ],
            keys: {
                x: 'name', // it's possible to specify 'x' when category axis
                value: ['upload', 'download']
            }
            };



        
            
         

        };
    
        
        

    componentDidMount() {

        axios.get(`https://covid19.mathdro.id/api/daily`)
        .then(response => response.data)
        .then((data) => {
          this.setState({ json: data })
         })
    }


    render() {
        const { currentDate, summary } = this.props;

        const chartData = {
            json: [
                {name: 'das', upload: 200, download: 200, total: 400},
                {name: 'dsad', upload: 100, download: 300, total: 400},
                {name: 'fsf', upload: 300, download: 200, total: 500},
                {name: 'wdasdsa', upload: 400, download: 100, total: 500},
            ],
            keys: {
                x: 'name',
                 // it's possible to specify 'x' when category axis
                value: ['upload', 'download']
            }
          };


        console.log(this.state.json);
        return (



            <div class= "p-grid">
                
            <div class="p-col-6" style={{ textAlign: 'center'}}>

            <Card>
            
            <C3Chart axis= {{
    x: {
        type: 'category'
       
    }
  }} data={this.state} />
            
                </Card>

            </div>
    
            </div>
        

        );
    }
}

export default Charts;
