import React, { Component } from 'react';
import { Card } from 'primereact/card';
import C3Chart from 'react-c3js';
import 'c3/c3.css';

class Charts extends Component {

    constructor() {
        super();
        this.state = {
            countries: [
                { field: "Country", header: "Country" },
                { field: "TotalConfirmed", header: 'Infected' },
                { field: "TotalDeaths", header: 'Deaths' },
                { field: "TotalRecovered", header: 'Recovered' }

            ]
        };

        data = {
            columns: [
              [500, 100],
              [50,30]
            ]
          };
    
        
        
    }


    render() {
        const { currentDate, summary } = this.props;

    
        return (



            <div class= "p-grid">
                
            <div class="p-col-6" style={{ textAlign: 'center'}}>

            <Card>
            <C3Chart data={this.state.columns} />
            
                </Card>

            </div>
    
            </div>
        

        );
    }
}

export default Charts;
