import React, { Component } from 'react';
import { Card } from 'primereact/card';
import C3Chart from 'react-c3js';
import 'c3/c3.css';

class BarChart extends Component {

    constructor(props) {
        super(props);

    
        this.state = {
            
            

        };

    };


    render() {
        console.log(this.props.summary);
        return (
           

            

             

                    <Card>

                    <C3Chart data={this.state.dat = {json: 
                            [this.props.summary]
                      
                        
                        ,
                        
                            keys: {
                              
                
                                value: ['TotalConfirmed', 'TotalDeaths']
                            },
                            names: {
                                TotalConfirmed: 'Infected',
                                TotalDeaths: 'Deaths'
                
                            },

                            type: 'bar' ,

                            colors: {
                                TotalConfirmed: 'red',
                                TotalDeaths: 'black',
                                
                            },
                
                        
                        }} />

                    </Card>

            

           


        );
    }
}

export default BarChart;
