import React, { Component } from 'react';
import {Card} from 'primereact/card';

class Hlavnicards extends Component {


    render() {
        const { currentDate, summary } = this.props;
        return (

            <div class="p-grid">
                <div class="p-col-12" style={{textAlign: 'center'}}>{new Date (currentDate).toDateString()}</div>
                <div class="p-col-4" style={{textAlign: 'center'}}>{summary.TotalConfirmed}</div>
                <div class="p-col-4" style={{textAlign: 'center'}}>{summary.TotalDeaths}</div>
                <div class="p-col-4" style={{textAlign: 'center'}}>{summary.TotalRecovered}</div>
            </div>

        );
    }
}

export default Hlavnicards;
