import React, { Component } from 'react';
import { Card } from 'primereact/card';
import C3Chart from 'react-c3js';
import 'c3/c3.css';
import axios from 'axios';
class Charts extends Component {

    constructor(props) {
        super(props);

    
        this.state = {

            json: [],


            keys: {
                x: 'reportDate',

                value: ['totalConfirmed', 'deaths.total']
            },
            names: {
                totalConfirmed: 'Infected',
                'deaths.total': 'Deaths'

            },
            types: {
                totalConfirmed: 'area-spline',
                'deaths.total': 'area-spline'
                // 'line', 'spline', 'step', 'area', 'area-step' are also available to stack
            },
            colors: {
                totalConfirmed: 'red',
                'deaths.total': 'black',
                
            },
            zoom: {
                enabled: true
            }
            



        };







    };



    componentDidMount() {
        
        console.log(this.props.jsons);   
        
        axios.get(`https://covid19.mathdro.id/api/daily`)
        .then(response => response.data)

        .then((data) => {
            this.setState({ json: data })
      
        })

        
        // this.setState({json: this.props.jsons});
    }


    render() {
       
        console.log(this.props);
        
        return (

            

            <div class="p-grid">

                <div class="p-col-6" style={{ textAlign: 'center' }}>

                    <Card>

                        <C3Chart axis={{
                            x: {
                                type: 'timeseries',
                                label: {
                                    text: 'Date',
                                    position: 'outer-center'
                                }
                            },
                            y: {
                                label: {
                                    text: 'Number of people',
                                    position: 'outer-middle'
                                }
                            },

                        }} data={this.props.jsons} />

                    </Card>

                </div>

            </div>


        );
    }
}

export default Charts;
