import React, { Component } from 'react';
import { Card } from 'primereact/card';
import C3Chart from 'react-c3js';
import 'c3/c3.css';
import rd3 from 'react-d3-library';

class D3chart extends Component {

    constructor(props) {
        super(props);

    
        this.state = {
            
            

        };

    };


    render() {
        console.log(this.props.summary);
        return (
           

            

                <div class="p-col-12" style={{ textAlign: 'center' }}>

                    <Card>

                    <C3Chart data={this.state.dat = {json: 
                            [this.props.summary]
                      
                        
                        ,
                        
                            keys: {
                              
                
                                value: ['TotalConfirmed', 'TotalDeaths']
                            },
                            names: {
                                TotalConfirmed: 'Infected',
                                TotalDeaths: 'Deaths'
                
                            },

                            type: 'donut' ,

                            colors: {
                                TotalConfirmed: 'red',
                                TotalDeaths: 'black',
                                
                            },
                
                        
                        }} />

                    </Card>

                </div>

           


        );
    }
}

export default D3chart;
