import React, { Component } from 'react';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import { Card } from 'primereact/card';
import {InputText} from 'primereact/inputtext';

class Datatable extends Component {

    constructor() {
        super();
        this.state = {
            countries: [
                { field: "Country", header: "Country" },
                { field: "TotalConfirmed", header: 'Infected' },
                { field: "TotalDeaths", header: 'Deaths' },
                { field: "TotalRecovered", header: 'Recovered' }

            ]

        };
    }


    render() {
        const { summaryCountries } = this.props;
        const header = (
            <div style={{'textAlign':'left'}}>
                <i className="pi pi-search" style={{margin:'4px 4px 0 0'}}></i>
                <InputText type="search" onInput={(e) => this.setState({globalFilter: e.target.value})} placeholder="Global Search" size="50"/>
            </div>
        );

        let dynamicColumns = this.state.countries.map((col) => {
            return <Column field={col.field} header={col.header} sortable={true} />;
        });

        return (

            <Card>
                <DataTable value={summaryCountries} header={header}
                globalFilter={this.state.globalFilter} emptyMessage="No records found"> 
                    {dynamicColumns}
                </DataTable>
            </Card>
        );
    }
}

export default Datatable;
