import React, { Component } from 'react';
import axios from 'axios';

 class PersonList extends Component  {

  constructor() {
    super();
    this.state = {
      users: [],
      global: [],
      date: []
    };
}

   
      componentDidMount() {
        const url = `api/corona2`;
        axios.get(url).then(response => response.data)
        .then((data) => {
          this.setState({ users: data.Countries })
          this.setState({ global: data.Global })
          this.setState({ date: data.Date })
          console.log(this.state.global)
         })
      }
      render() {
        return (
           <div className="container">
            <div className="col-xs-8">
            <h1>Current date {new Date (this.state.date).toDateString()}</h1>
            <h1>Total cases {this.state.global.TotalConfirmed}</h1>
            <h1>New cases {this.state.global.NewConfirmed}</h1>
            
            {this.state.users.map((user) => (
              <div className="card">
               <div className="card-body">
               <h5 className="card-title">{user.Country} - {user.TotalConfirmed}</h5>   
                        
                </div>
              </div>
            ))}
            </div>
           </div>
        );
      }
}
export default PersonList;
