import React, { Component } from 'react';
import { Card } from 'primereact/card';
import C3Chart from 'react-c3js';
import 'c3/c3.css';
import axios from 'axios';
class Charts extends Component {

    constructor() {
        super();
        this.state = {

            json: [],
            selectedColumns: []
         

        };



        this.state.data = {
            json: [

                {name: 'www.site1.com', upload: 200, download: 200, total: 400},
                {name: 'www.site2.com', upload: 100, download: 300, total: 400},
                {name: 'www.site3.com', upload: 300, download: 200, total: 500},
                {name: 'www.site4.com', upload: 400, download: 100, total: 500}
              
            ],
            keys: {
                // x: 'name', // it's possible to specify 'x' when category axis
                value: ['upload', 'download']
              }
          };
    
        
        
    }

    componentDidMount() {

        axios.get(`api/corona2`)
        .then(response => response.data)
        .then((data) => {
          this.setState({ selectedColumns: data.Global })
         })
    }


    render() {
        const { currentDate, summary } = this.props;


        console.log(this.state.global)
        return (



            <div class= "p-grid">
                
            <div class="p-col-6" style={{ textAlign: 'center'}}>

            <Card>
                <h1>{this.state.selectedColumns.TotalConfirmed}</h1>
            <C3Chart data={this.state.data} />
            
                </Card>

            </div>
    
            </div>
        

        );
    }
}

export default Charts;
