import React, { Component } from 'react';
import axios from 'axios';
import Hlavnicards from './Hlavnicards';
import Datatable from './Datatable';
import Charts from './Chart';
import DonutChart from './DonutChart';
import Countrypicker from './Countrypicker';
import BarChart from './BarChart';

 class Api extends Component  {

  constructor(props) {
    super(props);
    this.state = {
      countries: [],
      global: [],
      date: [],
      chartdata: [],
      json: []

    };
}

   
      componentDidMount() {

        axios.get(`https://covid19.mathdro.id/api/daily`)
        .then(response => response.data)
        .then((data) => {
            this.setState({ json: data })
             
        })
        
        axios.get(`api/corona2`)
        .then(response => response.data)
        .then((data) => {
          this.setState({ countries: data.Countries })
          this.setState({ global: data.Global })
          this.setState({ date: data.Date })
          
         })

         
      }

      render() {
        return (
            
          <div class="p-grid">      
          <div class="p-col-12" style={{textAlign: 'center', borderColor: 'black', borderStyle: 'solid'}}><h1>Covid - 19</h1></div>
          <div class= "p-col-12"><Hlavnicards currentDate = {this.state.date} summary = {this.state.global} /></div>
          <div class= "p-col-12"><Countrypicker /></div>

            <div class="p-col-12">
              <div class="p-grid">
                <div class="p-col-6" style={{ textAlign: 'center' }}>
                  <Charts json={this.state.json} />
                </div>
                <div class="p-col-6" style={{ textAlign: 'center' }}>
                  <DonutChart summary={this.state.global} />
                </div>
              </div>
            </div>
       
          <div class= "p-col-12"><Datatable summaryCountries = {this.state.countries} /></div>
          <div class="p-col-12" style={{textAlign: 'center', borderColor: 'black', borderStyle: 'solid'}}><h1>Copyright</h1></div>
          </div>

          
              
       
        );
      }
}

export default Api;
