import React, { Component } from 'react';
import axios from 'axios';
import Hlavnicards from './Hlavnicards';
import Datatable from './Datatable';
import Chart from './Chart';

 class Api extends Component  {

  constructor() {
    super();
    this.state = {
      countries: [],
      global: [],
      date: []
    };
}

   
      componentDidMount() {
        
        axios.get(`api/corona2`)
        .then(response => response.data)
        .then((data) => {
          this.setState({ countries: data.Countries })
          this.setState({ global: data.Global })
          this.setState({ date: data.Date })
          console.log(this.state.countries)
         })
      }

      render() {
        return (
            <div>
          <div class= "p-col-12"><Hlavnicards currentDate = {this.state.date} summary = {this.state.global} /></div>
          <div class= "p-col-12"><Chart currentDate = {this.state.date} summary = {this.state.global} /></div>
          <div class= "p-col-12"><Datatable currentDate = {this.state.date} summary = {this.state.global} /></div>
          </div>
              
       
        );
      }
}

export default Api;
