import React, { Component } from 'react';
import {DataTable} from 'primereact/datatable';
import {Column} from 'primereact/column';
import { Card } from 'primereact/card';

class Datatable extends Component {

    constructor() {
        super();
        this.state = {
            representatives: [
                {field: "Country", header: "Country"},
                {field: "TotalConfirmed", header: 'Infected'},
                {field: "TotalDeaths", header:'Deaths'},
                {field: "TotalRecovered", header:'Recovered'}
                
            ]

                
            
        };
    }


    render() {
        const { summaryCountries, currentDate } = this.props;

        let dynamicColumns = this.state.representatives.map((col) => {
            return <Column field={col.field} header={col.header} />;
        });

        return (
            
            <Card>   
                
            <DataTable value={summaryCountries}>
            {dynamicColumns}
        </DataTable>
        </Card> 
        );
    }
}

export default Datatable;
