import React, { Component } from 'react';
import axios from 'axios';
import Api from './Api.js';

 class Hlavnicards extends Component  {

  constructor() {
    super();
    this.state = {
      countries: [],
      global: [],
      date: []
    };
}

   
      componentDidMount() {
        
        axios.get(`api/corona2`)
        .then(response => response.data)
        .then((data) => {
          this.setState({ countries: data.Countries })
          this.setState({ global: data.Global })
          this.setState({ date: data.Date })
          console.log(this.state.countries)
         })
      }

      render() {
        return (
           <div>
            
            <h1>Current date {new Date (this.state.date).toDateString()}</h1>
            <h1>Total cases {this.state.state.TotalConfirmed}</h1>
            <h1>New cases {this.state.global.NewConfirmed}</h1>
            
            {this.state.countries.map((country) => (
         
               <h5 className="card-title">{country.Country} - {country.TotalConfirmed}</h5>   
                                    
            ))}
          
           </div>
        );
      }
}

export default Hlavnicards;
